$('#modalSpeaker').on('show.bs.modal', function (modal) {
    var id = modal.relatedTarget.getAttribute("data-id");
    var $curentModal = $(modal.currentTarget);

    if (SpeakersObj.hasOwnProperty(id)) {
        $curentModal.find(".modal-title").html(SpeakersObj[id].GioiThieu.TieuDe);
        $curentModal.find(".modal-body .c-speakers-detail").html(`<div class='b-grid'><div class="b-grid__img"><figure><img src="../images/upload/speakers/${SpeakersObj[id].HinhAnh}" alt="${SpeakersObj[id].HoTen}"/></figure></div><div class="b-grid__content"><div class="b-maincontent">${SpeakersObj[id].GioiThieu.NoiDung}</div></div></div>`);
    }
    else {
        $curentModal.find(".modal-title").html('');
        $curentModal.find(".modal-body .c-speakers-detail").html('');
    }
});